# Register: adgpos
from typing import List
from core.gunnershell.bofs.base import Bof, register
from colorama import Style, Fore

brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
brightyellow = "\001" + Style.BRIGHT + Fore.YELLOW + "\002"
reset = Style.RESET_ALL

def _is_ip(s: str) -> bool:
    if not s: return False
    s = s.strip("[]")
    parts = s.split(".")
    if len(parts) == 4:
        try:
            return all(0 <= int(p) <= 255 for p in parts)
        except ValueError:
            pass
    if ":" in s:
        hextets = s.split(":")
        if 2 <= len(hextets) <= 8 and all((h == "" or (1 <= len(h) <= 4 and all(c in "0123456789abcdefABCDEF" for c in h))) for h in hextets):
            return True
    return False

@register("adgpos")
class AdGposBof(Bof):
    base64bof: str = ""
    x86base64bof: str = ""

    def help_menu():
        menu = """
adgpos — Enumerate Group Policy Objects (GPOs)

Purpose
  See applied policies and possible misconfigurations. Defaults to listing all properties unless attributes are specified.

Usage
  bofexec adgpos [-d <domain>] [--dc <hostname>] [--dc-ip <ip>] [--ldaps] [--port <int>] \\
                 [--linked-only] [-a <attr,attr|*>] [-f <LdapFilter>]

Connection
  -d, --domain <dns>    DNS name of the target AD domain (e.g., contoso.com). Used for DC discovery/BaseDN.
  --dc <hostname>       DC hostname/FQDN (preferred when targeting a specific DC).
  --dc-ip <ip>          DC IP address (optional alternative).
  --ldaps               Use LDAPS (default 636). Else LDAP (default 389).
  --port <int>          Override port.

Selection & Output
  --linked-only         Only return GPOs that are linked to OUs or the domain via gPLink.
  -a, --attributes      CSV attributes or "*" for all (default "*"), e.g. "displayName,gPCFileSysPath"
  -f, --filter          Custom LDAP filter (verbatim). In --linked-only mode, this is applied on each GPO (BASE scope).

Defaults
  • Non-linked filter: (&(objectCategory=groupPolicyContainer)(objectClass=groupPolicyContainer))
  • Linked-only scan filter (OUs/domain): (&(|(objectClass=organizationalUnit)(objectClass=domainDNS))(gPLink=*))
Examples
  bofexec adgpos -a "displayName,gPCFileSysPath,versionNumber"
  bofexec adgpos --linked-only -a "displayName,flags,gPCFileSysPath"
  bofexec adgpos --linked-only -f "(displayName=*Drive*)" -a "displayName,gPCFileSysPath"
"""
        print(brightgreen + menu + reset)
        return

    @staticmethod
    def args_inject(p):
        # Connection / discovery
        p.add_argument("-d", "--domain", default="", help="DNS domain (e.g., contoso.com)")
        p.add_argument("--dc", default="", help="Domain Controller hostname/FQDN")
        p.add_argument("--dc-ip", default="", help="Domain Controller IP address")
        p.add_argument("--ldaps", action="store_true", default=False, help="Use LDAPS (default 636)")
        p.add_argument("--port", type=int, default=0, help="LDAP/LDAPS port (0 => default by scheme)")

        # Search / output
        p.add_argument("--linked-only", action="store_true", default=False, help="Only GPOs linked to OUs/domain")
        p.add_argument("-a", "--attributes", dest="attributes", default="*", help='CSV of attributes, or "*" for all (default "*")')
        p.add_argument("-f", "--filter", dest="filter", default="", help="Custom LDAP filter")

    @staticmethod
    def build_remote_args(ns) -> List[str]:
        dc_host  = getattr(ns, "dc", "") or ""
        dc_ip    = getattr(ns, "dc_ip", "") or ""
        domain   = getattr(ns, "domain", "") or ""
        if dc_host and _is_ip(dc_host):
            raise ValueError("--dc expects a hostname/FQDN. Use --dc-ip for IPs.")

        use_ldaps   = 1 if getattr(ns, "ldaps", False) else 0
        port_in     = int(getattr(ns, "port", 0) or 0)
        port        = port_in if port_in > 0 else (636 if use_ldaps else 389)

        linkedOnly  = 1 if getattr(ns, "linked_only", False) else 0
        attrsCSV    = getattr(ns, "attributes", "*") or "*"
        filt        = getattr(ns, "filter", "") or ""

        # Pack order must match entry.c
        return [
            f"-Z:{dc_host}",     # 1
            f"-Z:{dc_ip}",       # 2
            f"-Z:{domain}",      # 3
            f"-i:{use_ldaps}",   # 4
            f"-i:{port}",        # 5
            f"-i:{linkedOnly}",  # 6
            f"-Z:{attrsCSV}",    # 7
            f"-Z:{filt}",        # 8
        ]
