# Register: adous
from typing import List
from core.gunnershell.bofs.base import Bof, register
import argparse
from colorama import Style, Fore

brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
brightyellow = "\001" + Style.BRIGHT + Fore.YELLOW + "\002"
reset = Style.RESET_ALL

def _is_ip(s: str) -> bool:
    if not s: return False
    s = s.strip("[]")  # allow [::1] style
    parts = s.split(".")
    if len(parts) == 4:
        try:
            return all(0 <= int(p) <= 255 and (p == "0" or not p.startswith("0") or p == "0") for p in parts)
        except ValueError:
            pass
    if ":" in s:
        hextets = s.split(":")
        if 2 <= len(hextets) <= 8 and all((h == "" or (1 <= len(h) <= 4 and all(c in "0123456789abcdefABCDEF" for c in h))) for h in hextets):
            return True
    return False

def _space_escape(s: str) -> str:
    # percent-escape spaces so the BOF can decode and RFC4515-escape to \20
    return (s or "").replace(" ", "%20")

@register("adous")
class AdOUsBof(Bof):
    # NOTE: fill these with your compiled BOF blobs if your framework expects them
    base64bof: str = ""
    x86base64bof: str = ""

    @staticmethod
    def help_menu():
        menu = """
adous — Enumerate Organizational Units (OUs)

Purpose
  Map OU structure and (optionally) show linked GPOs.

Usage
  bofexec adous [--ou <name|*>] [--names] [-d <domain>] [--dc <hostname>] [--dc-ip <ip>] [--ldaps] [--port <int>] \\
                [--basedn <DN>] [-f <LdapFilter>] [-a <attr,attr,...>] [--linked-gpos]

Connection
  -d, --domain <dns>    DNS name of the target AD domain (e.g., contoso.com).
  --dc <hostname>       DC hostname/FQDN.
  --dc-ip <ip>          DC IP address.
  --ldaps               Use LDAPS (default port 636). Else LDAP (default 389).
  --port <int>          Override port.

Selection & Output
  --ou <name|*>         Specific OU by 'ou' or 'name'. "*" (default) = all OUs.
  --names               List only OU names (ou/name), one per line.
  --basedn <DN>         Custom Base DN. Default: from --domain or RootDSE.
  -f, --filter          Custom LDAP filter. Default: (objectClass=organizationalUnit)
                        If --ou is set (not "*"), ANDs with (|(ou=<ou>)(name=<ou>)).
  -a, --attributes      CSV attributes, or "*" for all (default: "*").
  --linked-gpos         Parse gPLink and resolve linked GPO names.

Notes
  • OU spaces are escaped as %20 in the loader, then RFC4515-escaped in BOF (SPACE -> \\20).

Examples
  bofexec adous --names
  bofexec adous --ou "Workstations" --linked-gpos -a "ou,distinguishedName,gPLink"
"""
        print(brightgreen + menu + reset)
        return

    @staticmethod
    def args_inject(p: argparse.ArgumentParser):
        # Connection / discovery
        p.add_argument("-d", "--domain", default="", help="DNS domain (e.g., contoso.com)")
        p.add_argument("--dc", default="", help="Domain Controller hostname/FQDN")
        p.add_argument("--dc-ip", default="", help="Domain Controller IP address")
        p.add_argument("--ldaps", action="store_true", default=False, help="Use LDAPS (default 636)")
        p.add_argument("--port", type=int, default=0, help="LDAP/LDAPS port (0 => default by scheme)")

        # Search / output
        p.add_argument("--basedn", default="", help="Base DN (default: from --domain or RootDSE)")
        p.add_argument("-f", "--filter", dest="filter", default="", help="Custom LDAP filter (overrides default OU filter)")
        p.add_argument("--ou", default="*", help="OU name (ou or name), or '*' for all")
        p.add_argument("--names", action="store_true", default=False, help="List only OU names (ou/name)")
        p.add_argument("-a", "--attributes", dest="attributes", default="*", help='CSV of attributes or "*" (default "*")')
        p.add_argument("--linked-gpos", action="store_true", default=False, help="Resolve/print linked GPOs (gPLink)")

    @staticmethod
    def build_remote_args(ns) -> List[str]:
        # Prefer --dc over --dc-ip if both supplied
        dc_host  = getattr(ns, "dc", "") or ""
        dc_ip    = getattr(ns, "dc_ip", "") or ""
        domain   = getattr(ns, "domain", "") or ""

        if dc_host and _is_ip(dc_host):
            raise ValueError("--dc expects a hostname/FQDN. Use --dc-ip for IPs.")

        use_ldaps = 1 if getattr(ns, "ldaps", False) else 0
        port_in   = int(getattr(ns, "port", 0) or 0)
        port      = port_in if port_in > 0 else (636 if use_ldaps else 389)

        base_dn   = getattr(ns, "basedn", "") or ""
        filt      = getattr(ns, "filter", "") or ""
        ou        = getattr(ns, "ou", "*") or "*"
        namesOnly = 1 if getattr(ns, "names", False) else 0
        attrsCSV  = getattr(ns, "attributes", "*") or "*"
        linked    = 1 if getattr(ns, "linked_gpos", False) else 0

        # Pack in exact BOF order/types (see header in entry.c):
        # 1 -Z dcHostW, 2 -Z dcIpW, 3 -Z domainW, 4 -i useLdaps, 5 -i port,
        # 6 -Z baseDN,  7 -Z filter,  8 -Z ouName, 9 -i namesOnly, 10 -Z attrsCSV, 11 -i linked
        return [
            f"-Z:{dc_host}",
            f"-Z:{dc_ip}",
            f"-Z:{domain}",
            f"-i:{use_ldaps}",
            f"-i:{port}",
            f"-Z:{base_dn}",
            f"-Z:{filt}",
            f"-Z:{_space_escape(ou)}",
            f"-i:{namesOnly}",
            f"-Z:{attrsCSV}",
            f"-i:{linked}",
        ]
