# Register: adpasswords
from typing import List
from core.gunnershell.bofs.base import Bof, register
from colorama import Style, Fore

brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
reset = Style.RESET_ALL

def _is_ip(s: str) -> bool:
    if not s: return False
    s = s.strip("[]")
    parts = s.split(".")
    if len(parts) == 4:
        try:
            return all(0 <= int(p) <= 255 for p in parts)
        except ValueError:
            pass
    if ":" in s:
        hextets = s.split(":")
        if 2 <= len(hextets) <= 8 and all((h == "" or (1 <= len(h) <= 4 and all(c in "0123456789abcdefABCDEF" for c in h))) for h in hextets):
            return True
    return False

@register("adpasswords")
class AdPasswordsBof(Bof):
    base64bof: str = ""   
    x86base64bof: str = ""

    def help_menu():
        menu = """
adpasswords — Identify Accounts with Interesting Password Policies

Purpose
  Detect weak password policy, stale/at-risk accounts.

Usage
  bofexec adpasswords [-d <domain>] [--dc <hostname>] [--dc-ip <ip>] [--ldaps] [--port <int>] \\
                      [--expired] [--never-expire] [--no-preauth] \\
                      [-a <attr,attr|*>] [--filter <LdapFilter>]

Connection
  -d, --domain <dns>    DNS name of the target AD domain (e.g., contoso.com). Used for DC discovery/BaseDN.
  --dc <hostname>       DC hostname/FQDN (preferred when targeting a specific DC).
  --dc-ip <ip>          DC IP address (optional alternative).
  --ldaps               Use LDAPS (default 636). Else LDAP (default 389).
  --port <int>          Override port.

Selection
  --expired             Password expired or must-change (pwdLastSet=0 or msDS-UserPasswordExpiryTimeComputed<=now).
  --never-expire        Accounts with "password never expires" (UAC 0x10000).
  --no-preauth          Accounts without Kerberos preauthentication (UAC 0x400000).
  -a, --attributes      CSV attributes or "*" for all (default "*").
  --filter              Extra LDAP filter to AND with the base (optional, verbatim).

Notes
  • Multiple flags are OR’d together (e.g., --expired --no-preauth returns either).
  • If you restrict -a, the BOF auto-adds key attributes for clarity.
Examples
  bofexec adpasswords --expired -a "sAMAccountName,msDS-UserPasswordExpiryTimeComputed,distinguishedName"
  bofexec adpasswords --never-expire --no-preauth -a "sAMAccountName,userAccountControl"
"""
        print(brightgreen + menu + reset)
        return

    @staticmethod
    def args_inject(p):
        # Connection
        p.add_argument("-d", "--domain", default="", help="DNS domain (e.g., contoso.com)")
        p.add_argument("--dc", default="", help="Domain Controller hostname/FQDN")
        p.add_argument("--dc-ip", default="", help="Domain Controller IP address")
        p.add_argument("--ldaps", action="store_true", default=False, help="Use LDAPS (default 636)")
        p.add_argument("--port", type=int, default=0, help="LDAP/LDAPS port (0 => default by scheme)")

        # Selection
        p.add_argument("--expired", action="store_true", default=False, help="Expired or must-change passwords")
        p.add_argument("--never-expire", action="store_true", default=False, help="Password never expires (UAC 0x10000)")
        p.add_argument("--no-preauth", action="store_true", default=False, help="No Kerberos preauthentication (UAC 0x400000)")

        # Output
        p.add_argument("-a", "--attributes", dest="attributes", default="*", help='CSV of attributes, or "*" for all (default "*")')
        p.add_argument("--filter", default="", help="Extra LDAP filter to AND with the base (verbatim)")

    @staticmethod
    def build_remote_args(ns) -> List[str]:
        dc_host  = getattr(ns, "dc", "") or ""
        dc_ip    = getattr(ns, "dc_ip", "") or ""
        domain   = getattr(ns, "domain", "") or ""
        if dc_host and _is_ip(dc_host):
            raise ValueError("--dc expects a hostname/FQDN. Use --dc-ip for IPs.")

        use_ldaps   = 1 if getattr(ns, "ldaps", False) else 0
        port_in     = int(getattr(ns, "port", 0) or 0)
        port        = port_in if port_in > 0 else (636 if use_ldaps else 389)

        wantExpired = 1 if getattr(ns, "expired", False) else 0
        wantNever   = 1 if getattr(ns, "never_expire", False) else 0
        wantNoPA    = 1 if getattr(ns, "no_preauth", False) else 0

        attrsCSV    = getattr(ns, "attributes", "*") or "*"
        extraFilter = getattr(ns, "filter", "") or ""

        # Pack order must match entry.c
        return [
            f"-Z:{dc_host}",     # 1
            f"-Z:{dc_ip}",       # 2
            f"-Z:{domain}",      # 3
            f"-i:{use_ldaps}",   # 4
            f"-i:{port}",        # 5
            f"-i:{wantExpired}", # 6
            f"-i:{wantNever}",   # 7
            f"-i:{wantNoPA}",    # 8
            f"-Z:{attrsCSV}",    # 9
            f"-Z:{extraFilter}", # 10
        ]
