# Register: adspns
from typing import List
from core.gunnershell.bofs.base import Bof, register
from colorama import Style, Fore

brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
brightyellow = "\001" + Style.BRIGHT + Fore.YELLOW + "\002"
reset = Style.RESET_ALL

def _is_ip(s: str) -> bool:
    if not s: return False
    s = s.strip("[]")
    parts = s.split(".")
    if len(parts) == 4:
        try:
            return all(0 <= int(p) <= 255 for p in parts)
        except ValueError:
            pass
    if ":" in s:
        hextets = s.split(":")
        if 2 <= len(hextets) <= 8 and all((h == "" or (1 <= len(h) <= 4 and all(c in "0123456789abcdefABCDEF" for c in h))) for h in hextets):
            return True
    return False

@register("adspns")
class AdSpnsBof(Bof):
    base64bof: str = ""
    x86base64bof: str = ""

    def help_menu():
        menu = """
adspns â€” Enumerate Service Principal Names (SPNs)

Purpose
  Kerberoasting prep: find services tied to accounts.

Usage
  bofexec adspns [-d <domain>] [--dc <hostname>] [--dc-ip <ip>] [--ldaps] [--port <int>] \\
                 [--cifs] [--http] [--mssql] [--user-only|--computer-only] \\
                 [--filter <LdapFilter>] [--attributes <csv|*>]

Connection
  -d, --domain <dns>    DNS name of the target AD domain (e.g., contoso.com). Used for DC discovery/BaseDN.
  --dc <hostname>       DC hostname/FQDN (preferred).
  --dc-ip <ip>          DC IP address (optional alternative).
  --ldaps               Use LDAPS (default 636). If not set -> LDAP (default 389).
  --port <int>          Override port.

Selection & Output
  --cifs                Only return CIFS SPNs   (servicePrincipalName=cifs/*)
  --http                Only return HTTP SPNs   (servicePrincipalName=http/*)
  --mssql               Only return MSSQL SPNs  (servicePrincipalName=MSSQLSvc/*)
  --user-only           Restrict to user objects
  --computer-only       Restrict to computer objects
  --filter <filter>     Custom LDAP filter. If supplied, it is used verbatim (flags are ignored).
  --attributes <csv|*>  Attributes to print (default "*", i.e., all)

Examples
  bofexec adspns --cifs --attributes "servicePrincipalName,sAMAccountName"
  bofexec adspns --http --user-only
  bofexec adspns --mssql --computer-only
  bofexec adspns --filter "(&(objectClass=user)(servicePrincipalName=HOST/*))"
"""
        print(brightgreen + menu + reset)
        return

    @staticmethod
    def args_inject(p):
        # Connection / discovery
        p.add_argument("-d", "--domain", default="", help="DNS domain (e.g., contoso.com)")
        p.add_argument("--dc", default="", help="Domain Controller hostname/FQDN")
        p.add_argument("--dc-ip", default="", help="Domain Controller IP address")
        p.add_argument("--ldaps", action="store_true", default=False, help="Use LDAPS (default 636)")
        p.add_argument("--port", type=int, default=0, help="LDAP/LDAPS port (0 => default by scheme)")

        # Search / output
        p.add_argument("--cifs",   action="store_true", default=False, help="Only CIFS SPNs")
        p.add_argument("--http",   action="store_true", default=False, help="Only HTTP SPNs")
        p.add_argument("--mssql",  action="store_true", default=False, help="Only MSSQLSvc SPNs")
        p.add_argument("--user-only",     action="store_true", default=False, help="Users only")
        p.add_argument("--computer-only", action="store_true", default=False, help="Computers only")
        p.add_argument("--filter", default="", help="Custom LDAP filter (if supplied, flags are ignored)")
        p.add_argument("--attributes", default="*", help='CSV of attributes, or "*" for all (default "*")')

    @staticmethod
    def build_remote_args(ns) -> List[str]:
        dc_host  = getattr(ns, "dc", "") or ""
        dc_ip    = getattr(ns, "dc_ip", "") or ""
        domain   = getattr(ns, "domain", "") or ""
        if dc_host and _is_ip(dc_host):
            raise ValueError("--dc expects a hostname/FQDN. Use --dc-ip for IPs.")

        use_ldaps = 1 if getattr(ns, "ldaps", False) else 0
        port_in   = int(getattr(ns, "port", 0) or 0)
        port      = port_in if port_in > 0 else (636 if use_ldaps else 389)

        cifsOnly  = 1 if getattr(ns, "cifs", False) else 0
        httpOnly  = 1 if getattr(ns, "http", False) else 0
        mssqlOnly = 1 if getattr(ns, "mssql", False) else 0
        userOnly  = 1 if getattr(ns, "user_only", False) else 0
        compOnly  = 1 if getattr(ns, "computer_only", False) else 0

        filt      = getattr(ns, "filter", "") or ""
        attrsCSV  = getattr(ns, "attributes", "*") or "*"

        # Pack order must match entry.c
        return [
            f"-Z:{dc_host}",     # 1
            f"-Z:{dc_ip}",       # 2
            f"-Z:{domain}",      # 3
            f"-i:{use_ldaps}",   # 4
            f"-i:{port}",        # 5
            f"-i:{cifsOnly}",    # 6
            f"-i:{httpOnly}",    # 7
            f"-i:{mssqlOnly}",   # 8
            f"-i:{userOnly}",    # 9
            f"-i:{compOnly}",    # 10
            f"-Z:{attrsCSV}",    # 11
            f"-Z:{filt}",        # 12
        ]
