import base64
from typing import List

# BOF imports
from core.gunnershell.bofs.base import Bof, register

from colorama import Style, Fore
brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
brightyellow = "\001" + Style.BRIGHT + Fore.YELLOW + "\002"
reset = Style.RESET_ALL

@register("dumpsysmonconfig")
class DumpSysMonConfigBof(Bof):
	base64bof: str = ""

	def help_menu():
		menu = """
Reads Sysmon’s live configuration directly from the registry (no sysmon.exe, no child processes).
Locates the Sysmon driver by the presence of a Parameters\\Rules REG_BINARY value under HKLM\\SYSTEM\\CurrentControlSet\\Services\\<Driver>\\Parameters,
pairs it with the user-mode service via DriverName, then prints flags and dumps the compiled Rules blob (Base64) along with a SHA-256 for auditing.

Usage:
  bofexec dumpsysmonconfig

Output:
  Sysmon configuration (from registry)
    - Service name: Sysmon64
    - Driver name : SysmonDrv
    - Options     : 0x00000003  [Network enabled, ImageLoad enabled]
    - Hash algs   : SHA1 SHA256
    - Rules blob  : 123456 bytes, SHA-256 2a1b...ff

   Rules (base64)
   <base64 lines...>
"""
		print(brightgreen + menu + reset)
		return