import base64
from typing import List

# BOF imports
from core.gunnershell.bofs.base import Bof, register

# Session Management
from core.session_handlers import session_manager

# Command Execution Imports
from core.command_execution import http_command_execution as http_exec
from core.command_execution import tcp_command_execution as tcp_exec

from colorama import Style, Fore
brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
brightyellow = "\001" + Style.BRIGHT + Fore.YELLOW + "\002"
reset = Style.RESET_ALL

@register("dir")
class DirBof(Bof):
	base64bof: str = ""
	x86base64bof: str = ""

	def help_menu():
		menu = """
Lists a target directory using BOF.
Usage: bofexec dir -z [directory] -z [/s]
"""

		print(brightgreen + menu + reset)
		return
		
	@staticmethod
	def check_args(zargs: List[str] | None, Zargs: List[str] | None, int16: List[int] | None = None, int32: List[int] | None = None) -> list[str]:
		"""
		Return any default/extra args this BOF wants to inject if caller
		didn't supply them. Do NOT include quotes around -z values.
		"""
		zargs = zargs or []
		extra: list[str] = []
		if not zargs:
			extra.append("-z:.")
		return extra
