import base64
from typing import List

# BOF imports
from core.gunnershell.bofs.base import Bof, register

from colorama import Style, Fore
brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
brightyellow = "\001" + Style.BRIGHT + Fore.YELLOW + "\002"
reset = Style.RESET_ALL

@register("getrecentfiles")
class GetRecentFilesBof(Bof):
	base64bof:str = ""
	x86base64bof: str = ""

	def help_menu():
		menu = """
getrecentfiles — List recent files for current user

Description:
  Enumerates the user's Recent Items folder and displays when each item was used
  and where it points. Works without COM and avoids large stack frames (BOF-safe).

Usage:
  bofexec getrecentfiles

Notes:
  • Target path is best-effort from the .lnk’s LocalBasePath; some shortcuts
    (network, special targets) may not show a path.
  • Results are per-user and reflect the current user context.
"""
		print(brightgreen + menu + reset)
		return