from __future__ import annotations

# BOF imports
from core.gunnershell.bofs.base import Bof, register

from colorama import Style, Fore
brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
brightyellow = "\001" + Style.BRIGHT + Fore.YELLOW + "\002"
reset = Style.RESET_ALL

@register("netlocalgroup")
class NetLocalGroupBof(Bof):
	base64bof: str = ""
	x86base64bof: str = ""

	def help_menu():
		menu = """
List Groups in this server (or specified server if given)
Usage: bofexec netlocalgroup -z [Optional <opt: servername>]
"""
		print(brightgreen + menu + reset)
		return

	@staticmethod
	def check_args(zargs: List[str] | None, Zargs: List[str] | None, int16: List[int] | None = None) -> list[str]:
		int16 = int16 or []
		zargs = zargs or []
		extra: List[int] = []
		if not int16 and zargs:
			extra.append(f"-s:1")
		return extra