from __future__ import annotations

# BOF imports
from core.gunnershell.bofs.base import Bof, register

from colorama import Style, Fore
brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
brightyellow = "\001" + Style.BRIGHT + Fore.YELLOW + "\002"
reset = Style.RESET_ALL

@register("netuptime")
class NetUpTimeBof(Bof):
	base64bof: str = ""
	x86base64bof: str = ""

	def help_menu():
		menu = """
Returns information about the boot time on the local (or a remote) machine
Usage: bofexec netuptime -z [Optional: <\\computername>]
"""
		print(brightgreen + menu + reset)
		return