import base64
from typing import List

# BOF imports
from core.gunnershell.bofs.base import Bof, register

from colorama import Style, Fore
brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
brightyellow = "\001" + Style.BRIGHT + Fore.YELLOW + "\002"
reset = Style.RESET_ALL

@register("dotnetversion")
class DotNetVersionBof(Bof):
	base64bof: str = ""
	x86base64bof: str = ""

	def help_menu():
		menu = """
dotnetversion – Installed .NET versions

Usage:
  bofexec dotnetversion

Description:
  Enumerates installed Microsoft .NET components by reading HKLM registry keys:
    • .NET Framework (v2–v4.x):   HKLM\\SOFTWARE\\Microsoft\\NET Framework Setup\\NDP
    • .NET (Core/5+):
        HKLM\\SOFTWARE\\dotnet\\Setup\\InstalledVersions\\<arch>\\sdk
        HKLM\\SOFTWARE\\dotnet\\Setup\\InstalledVersions\\<arch>\\sharedfx\\{Microsoft.NETCore.App, Microsoft.WindowsDesktop.App, Microsoft.AspNetCore.App}

On 64-bit Windows, both 64-bit and 32-bit views are queried so you’ll see x64 and x86 installs.
No network/WMI required; registry only.
"""
		print(brightgreen + menu + reset)
		return