import base64
from typing import List

# BOF imports
from core.gunnershell.bofs.base import Bof, register

from colorama import Style, Fore
brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
brightyellow = "\001" + Style.BRIGHT + Fore.YELLOW + "\002"
reset = Style.RESET_ALL

@register("getkernaldrivers")
class GetKernalDriversBof(Bof):
	base64bof: str = ""
	x86base64bof: str = ""

	def help_menu():
		menu = """
getkernaldrivers  —  List loaded kernel drivers

Description:
  Enumerates currently loaded kernel-mode drivers and prints base address, 
  image name, and full path.

Usage:
  bofexec getkernaldrivers

Output columns:
  Base    - Image base address (kernel VA)
  Name    - Driver (module) file name
  Path    - Full filesystem path to the driver image
"""
		print(brightgreen + menu + reset)
		return