import base64
from typing import List

# BOF imports
from core.gunnershell.bofs.base import Bof, register

from colorama import Style, Fore
brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
brightyellow = "\001" + Style.BRIGHT + Fore.YELLOW + "\002"
reset = Style.RESET_ALL

@register("hotfixenum")
class HotFixEnumBof(Bof):
	base64bof: str = ""
	x86base64bof: str = ""

	def help_menu():
		menu = """
hotfixenum — List installed hotfixes / patch level

Usage:
  bofexec hotfixenum

What it does:
  • Queries WMI class Win32_QuickFixEngineering for KBs (ID, date, description)
  • Falls back to HKLM\\Software\\Microsoft\\Windows NT\\CurrentVersion\\Hotfix on older systems

Output columns:
  KB (HotFixID), InstalledOn, Description/Caption

Notes:
  • No arguments required
  • WMI must be accessible; otherwise registry fallback is used
  • Designed for BOF: small stack usage, manual API imports
"""
		print(brightgreen + menu + reset)
		return