import base64
from typing import List

# BOF imports
from core.gunnershell.bofs.base import Bof, register

from colorama import Style, Fore
brightgreen = "\001" + Style.BRIGHT + Fore.GREEN + "\002"
brightyellow = "\001" + Style.BRIGHT + Fore.YELLOW + "\002"
reset = Style.RESET_ALL

@register("listinstalled")
class ListInstalledBof(Bof):
	base64bof: str = ""
	x86base64bof: str = ""

	def help_menu():
		menu = """
listinstalled — Enumerate installed software (x86/x64)

Usage:
	bofexec listinstalled
	bofexec listinstalled -h | --help

Description:
	Lists installed software by reading the standard Uninstall registry keys:
		• HKLM\\SOFTWARE\\Microsoft\\Windows\\CurrentVersion\\Uninstall
		• HKLM\\SOFTWARE\\WOW6432Node\\Microsoft\\Windows\\CurrentVersion\\Uninstall
		• HKCU\\SOFTWARE\\Microsoft\\Windows\\CurrentVersion\\Uninstall
	On 64-bit Windows, both x64 and x86 views are included and merged.

Output:
	ProductName | Version | Publisher | InstallDate | Arch | UninstallString (when available)

Notes:
	• Some apps (MSIX/Store apps, drivers, portable tools) may not appear here.
	• Per-user installs are shown when present under HKCU.
	• InstallDate format varies by vendor.
"""
		print(brightgreen + menu + reset)
		return